import { useState } from 'react';
import { useStellar } from '@galaxy/core-stellar-sdk';
import { useInvisibleWallet } from '@galaxy/core-invisible-wallet';

const Home = () => {
  const { account, connect } = useStellar();
  const { wallet, createWallet } = useInvisibleWallet();
  const [isConnected, setIsConnected] = useState(false);

  const handleConnect = async () => {
    try {
      await connect();
      setIsConnected(true);
    } catch (error) {
      console.error('Failed to connect:', error);
    }
  };

  const handleCreateWallet = async () => {
    try {
      await createWallet();
    } catch (error) {
      console.error('Failed to create wallet:', error);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 flex items-center justify-center">
      <div className="max-w-md mx-auto bg-white rounded-xl shadow-md overflow-hidden">
        <div className="p-8">
          <div className="uppercase tracking-wide text-sm text-indigo-500 font-semibold">{{PROJECT_NAME}}</div>
          <h1 className="block mt-1 text-lg leading-tight font-medium text-black">Welcome to Galaxy DevKit</h1>
          <p className="mt-2 text-gray-500">Build Stellar DApps with ease</p>

          <div className="mt-6 space-y-4">
            {!isConnected ? (
              <button
                onClick={handleConnect}
                className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-2 px-4 rounded"
              >
                Connect to Stellar
              </button>
            ) : (
              <div className="text-green-600 font-semibold">Connected to Stellar!</div>
            )}

            {!wallet && (
              <button
                onClick={handleCreateWallet}
                className="w-full bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-4 rounded"
              >
                Create Invisible Wallet
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;

import '../styles/globals.css';
import type { AppProps } from 'next/app';
import { StellarProvider } from '@galaxy/core-stellar-sdk';
import { InvisibleWalletProvider } from '@galaxy/core-invisible-wallet';

function MyApp({ Component, pageProps }: AppProps) {
  return (
    <StellarProvider>
      <InvisibleWalletProvider>
        <Component {...pageProps} />
      </InvisibleWalletProvider>
    </StellarProvider>
  );
}

export default MyApp;

